import { ERROR } from "trophy-common-utils/Errors.mjs";

export const validatePathParametersUserIdWithEvent = function (event) {

    // Verfiy 'event exists' exists and is not null, throw error if not
    if (event === undefined || event === null) throw new Error(`${ERROR.ERROR_INVALID_LAMBDA_EVENT_UNDEFINED_NULL}`);

    // Verify that 'pathParameters' exists and is not null, throw error if not
    if (event.pathParameters === undefined || event.pathParameters === null) throw new Error(`${ERROR.ERROR_INVALID_LAMBDA_PATH_PARAMETERS_UNDEFINED_NULL}`);

    // Verify 'userId' pathParameter exists and is not null, throw error if not
    if (event.pathParameters.userId === undefined || event.pathParameters === null) throw new Error(`${ERROR.ERROR_INVALID_LAMBDA_PARAMETER_UNDEFINED_NULL}`);
}