/**
 * Compare two UUIDs, one with hyphens and one without, in a case-insensitive manner.
 *
 * @param {string} uuidWithHyphens - The UUID string with hyphens.
 * @param {string} uuidWithoutHyphens - The UUID string without hyphens.
 * @returns {boolean} Returns true if the UUIDs are equal after removing hyphens and ignoring case, otherwise false.
 */
export const compareUserId = (uuidWithHyphens, uuidWithoutHyphens) => {
    // If either are undefined or null, return false
    if (uuidWithHyphens === undefined || uuidWithHyphens === null) return false;
    if (uuidWithoutHyphens === undefined || uuidWithoutHyphens === null) return false;

    // Remove hyphens from the UUID with hyphens and convert to lowercase
    const uuidWithoutHyphensFromHyphenated = uuidWithHyphens.replace(/-/g, '').toLowerCase();

    // Convert the UUID without hyphens to lowercase
    const lowerCaseUuidWithoutHyphens = uuidWithoutHyphens.toLowerCase();

    // Compare the two UUIDs
    if (uuidWithoutHyphensFromHyphenated === lowerCaseUuidWithoutHyphens) {
        console.log("The UUIDs are equal.");
        return true;
    } else {
        console.log(`The UUIDs ${uuidWithoutHyphensFromHyphenated} and ${lowerCaseUuidWithoutHyphens} are not equal.`);
        return false;
    }
}

