import { EXERCISE_TYPES_TABLE } from '../trophy-common-utils/DatabaseTables.mjs';
import { EXERCISE_TYPE_ID_COLUMN, EXERCISE_TYPE_VALUE_COLUMN } from '../trophy-common-utils/DatabaseTableFields.mjs';

export const retrieveExerciseTypeValueID = async (exerciseTypeString, connection) => {
    try {

        const [rows, fields] = await connection.execute(
            `
            SELECT ${EXERCISE_TYPE_ID_COLUMN}
            FROM ${ EXERCISE_TYPES_TABLE } 
            WHERE  ${ EXERCISE_TYPE_VALUE_COLUMN } = ?
            `,
            [exerciseTypeString]);

        //Return if any rows were found
        return rows.length > 0 ? rows[0].exercise_type_id : null;

    } catch(error) {
        console.log("Error",error);
        return null;
    } finally {

    }
}