/**
 * Logs the details of an exercise object.
 * 
 * @param {Object} exercise - The exercise object to log.
 */
export const logExercise = (exercise) => {
    if (exercise && typeof exercise === 'object') {
        const { name, type, attributes, date, duration, notes } = exercise;

        if (name) console.log(`Name: ${name}`);
        if (type) console.log(`Type: ${type}`);
        if (attributes && typeof attributes === 'object') {
            const { distance, time, sets, reps, weight, intensity, level } = attributes;

            if (distance && typeof distance === 'object' && distance.value && distance.unit) {
                console.log(`Distance: ${distance.value} ${distance.unit}`);
            }
            if (time && typeof time === 'object' && time.value) {
                console.log(`Time: ${time.value}`);
            }
            if (sets && typeof sets === 'object' && sets.value) {
                console.log(`Sets: ${sets.value}`);
            }
            if (reps && typeof reps === 'object' && reps.value) {
                console.log(`Reps: ${reps.value}`);
            }
            if (weight && typeof weight === 'object' && weight.value && weight.unit) {
                console.log(`Weight: ${weight.value} ${weight.unit}`);
            }
            if (intensity && typeof intensity === 'object' && intensity.value) {
                console.log(`Intensity: ${intensity.value}`);
            }
            if (level && typeof level === 'object' && level.value) {
                console.log(`Level: ${level.value}`);
            }
        }
        if (date) console.log(`Date: ${date}`);
        if (duration) console.log(`Duration: ${duration}`);
        if (notes) console.log(`Notes: ${notes}`);
    }
};